// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    Data.Section section = vizcore3d.Section.SelectedItem;
    if (section == null) return;

    Data.BoundBox3D box = section.BoundBox;
    if (box == null) return;

    List<Data.Node> items = vizcore3d.Object3D.FromZone(
        box
        , Data.BoundBoxSearchOption.FullyContained
        );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    Data.Section section = vizcore3d.Section.SelectedItem;
    if (section == null) return;

    List<Data.Node> items = section.GetObject3D(Data.BoundBoxSearchOption.FullyContained);
}

private void Example2()
{
    Data.Section section = vizcore3d.Section.SelectedItem;
    if (section == null) return;

    if (section.SectionType != Manager.SectionManager.SectionTypes.SECTION_BOX) return;

    Data.BoundBox3D box = section.BoundBox;
    if (box == null) return;

    List<Data.Node> items = vizcore3d.Object3D.FromZone(box, Data.BoundBoxSearchOption.FullyContained);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example1()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Section.Sections.Count == 0) return;
    if (vizcore3d.Section.SelectedItem == null) return;

    VIZCore3D.NET.Data.Section item = vizcore3d.Section.SelectedItem;

    item.ExportVIZ(
        "C:\\Temp\\Section.viz" /* Path */
        , true                  /* Keep Structure */
        );
}

private void Example2()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Section.Sections.Count == 0) return;
    if (vizcore3d.Section.SelectedItem == null) return;

    VIZCore3D.NET.Data.Section item = vizcore3d.Section.SelectedItem;

    vizcore3d.Section.ExportVIZ(
        item                        /* Section(Box) */
        , "C:\\Temp\\Section.viz"   /* Path */
        , true                      /* Keep Structure */
        );
}

private void Example3()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Section.Sections.Count == 0) return;
