private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Disassemble()
{
    vizcore3d.Object3D.Disassembly.EnableAnimation = true;
    vizcore3d.Object3D.Disassembly.Distance = 10000;    // 10m
    vizcore3d.Object3D.Disassembly.SideGap = 1000;      // 1m
    vizcore3d.Object3D.Disassembly.Target = VIZCore3D.NET.Manager.DisassemblyManager.TargetKind.ALL;            // All Object
    //vizcore3d.Object3D.Disassembly.Target = VIZCore3D.NET.Manager.DisassemblyManager.TargetKind.SELECTED;     // Selected Object

    vizcore3d.Object3D.Disassembly.Option = VIZCore3D.NET.Manager.DisassemblyManager.DisassemblyOption.AXIS;    // 축 기준 분해
    //vizcore3d.Object3D.Disassembly.Option = VIZCore3D.NET.Manager.DisassemblyManager.DisassemblyOption.LINEAR;// 선형 분해
    //vizcore3d.Object3D.Disassembly.Option = VIZCore3D.NET.Manager.DisassemblyManager.DisassemblyOption.SPHERE;// 구 분해

    // If Option is DisassemblyOption.AXIS
    vizcore3d.Object3D.Disassembly.Axis = VIZCore3D.NET.Manager.DisassemblyManager.AxisKind.X_PLUS;

    vizcore3d.Object3D.Disassembly.Disassemble();
}


private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void AddGroup()
{
    List<VIZCore3D.NET.Data.Node> child =
        vizcore3d.Object3D.GetChildObject3d(
            0 /* Root Node Index */
            , VIZCore3D.NET.Data.Object3DChildOption.CHILD_ONLY   /* Sub Node */
            );

    for (int i = 0; i < child.Count; i++)
    {
        bool result = vizcore3d.Object3D.Disassembly.AddGroup(
            i                   /* ID : 0 ~ */
            , child[i].Index    /* NODE INDEX */
            , true              /* Recursive */
            );
    }
}

private void ClearGroup()
{
    vizcore3d.Object3D.Disassembly.ClearGroup();
}


private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void AddGroup()
{
    List<VIZCore3D.NET.Data.Node> child =
        vizcore3d.Object3D.GetChildObject3d(
            0 /* Root Node Index */
            , VIZCore3D.NET.Data.Object3DChildOption.CHILD_ONLY   /* Sub Node */
            );

    for (int i = 0; i < child.Count; i++)
    {
        bool result = vizcore3d.Object3D.Disassembly.AddGroup(
            i                   /* ID : 0 ~ */
            , child[i].Index    /* NODE INDEX */
            , true              /* Recursive */
            );
    }
}

private void ClearGroup()
{
    vizcore3d.Object3D.Disassembly.ClearGroup();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    AddGroup(0);

    DisassemblyByRate(0.5f);
}

private void AddGroup(int groupKind)
{
    int index = groupKind;

    List<VIZCore3D.NET.Data.Node> items = null;

    if (index == 0) // Children of Root
    {
        items = vizcore3d.Object3D.GetChildObject3d(
                    0                                                       /* Root Node Index */
                    , VIZCore3D.NET.Data.Object3DChildOption.CHILD_ONLY     /* Sub Node */
                    );


    }
    else if (index == 1) // Part (Leaf Node)
    {
        items = vizcore3d.Object3D.FromFilter(Data.Object3dFilter.PART);
    }
    else if (index == 2) // Leaf Assembly
    {
        items = vizcore3d.Object3D.FromFilter(Data.Object3dFilter.LEAF_ASSEMBLY);
    }
    else if (index == 3) // Parent of Leaf Assembly
    {
        items = vizcore3d.Object3D.FromFilter(Data.Object3dFilter.PARENT_LEAF_ASSEMBLY);
    }

    if (items == null) return;

    for (int i = 0; i < items.Count; i++)
    {
        bool result = vizcore3d.Object3D.Disassembly.AddGroup(
            i                   /* ID : 0 ~ */
            , items[i].Index    /* NODE INDEX */
            , true              /* Recursive */
            );
    }
}

private void DisassemblyByRate(float rate)
{
    vizcore3d.Object3D.Disassembly.DisassembleBySphereCenterDistanceRate(true, rate);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    List<VIZCore3D.NET.Data.Node> items =
        vizcore3d.Object3D.FromFilter(
            VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP
            );

    if (items.Count == 0) return;

    vizcore3d.GeometryUtility.OnOsnapPickingItem += GeometryUtility_OnOsnapPickingItem;

    vizcore3d.GeometryUtility.ShowOsnap(
        items[0].Index  /* Node Index */
        , false         /* Item : Surface */
        , true          /* Item : Vertex */
        , true          /* Item : Line */
        , true          /* Item : Circle */
        );
}

private void GeometryUtility_OnOsnapPickingItem(object sender, VIZCore3D.NET.Event.EventManager.OsnapPickingItemEventArgs e)
{
    VIZCore3D.NET.Data.OsnapKind kind = e.Kind;
    VIZCore3D.NET.Data.Vertex3D point = e.Point;
    VIZCore3D.NET.Data.Vertex3D start = e.Start;
    VIZCore3D.NET.Data.Vertex3D end = e.End;
    VIZCore3D.NET.Data.Vertex3D normal = e.Normal;
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    List<VIZCore3D.NET.Data.Node> items =
        vizcore3d.Object3D.FromFilter(
            VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP
            );

    if (items.Count == 0) return;

    vizcore3d.GeometryUtility.OnOsnapPickingItem += GeometryUtility_OnOsnapPickingItem;

    vizcore3d.GeometryUtility.ShowOsnap(
        items[0].Index  /* Node Index */
        , false         /* Item : Surface */
        , true          /* Item : Vertex */
        , true          /* Item : Line */
        , true          /* Item : Circle */
        );
}

private void GeometryUtility_OnOsnapPickingItem(object sender, VIZCore3D.NET.Event.EventManager.OsnapPickingItemEventArgs e)
{
    VIZCore3D.NET.Data.OsnapKind kind = e.Kind;
    VIZCore3D.NET.Data.Vertex3D point = e.Point;
    VIZCore3D.NET.Data.Vertex3D start = e.Start;
    VIZCore3D.NET.Data.Vertex3D end = e.End;
    VIZCore3D.NET.Data.Vertex3D normal = e.Normal;
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{   
    vizcore3d.GeometryUtility.OnOsnapPickingItem += GeometryUtility_OnOsnapPickingItem;

    vizcore3d.GeometryUtility.ShowOsnap(
        , false         /* Item : Surface */
        , true          /* Item : Vertex */
        , true          /* Item : Line */
        , true          /* Item : Circle */
        );
}

private void GeometryUtility_OnOsnapPickingItem(object sender, VIZCore3D.NET.Event.EventManager.OsnapPickingItemEventArgs e)
{
    VIZCore3D.NET.Data.OsnapKind kind = e.Kind;
    VIZCore3D.NET.Data.Vertex3D point = e.Point;
    VIZCore3D.NET.Data.Vertex3D start = e.Start;
    VIZCore3D.NET.Data.Vertex3D end = e.End;
    VIZCore3D.NET.Data.Vertex3D normal = e.Normal;
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // 이동되어야 할 라인
    VIZCore3D.NET.Data.Vertex3D v1 
        = new VIZCore3D.NET.Data.Vertex3D(-24.85, 254.20, 500);
    VIZCore3D.NET.Data.Vertex3D v2 
        = new VIZCore3D.NET.Data.Vertex3D(-450, -650, 500);

    // 고정 라인
    VIZCore3D.NET.Data.Vertex3D v3 
        = new VIZCore3D.NET.Data.Vertex3D(500, 500, 500);
    VIZCore3D.NET.Data.Vertex3D v4 
        = new VIZCore3D.NET.Data.Vertex3D(500, -500, 500);

    // 이동&회전 매트릭스 계산
    VIZCore3D.NET.Data.Matrix3D matrix 
        = vizcore3d.GeometryUtility.GetMatrixToMoveTo2PointParallelTo2Point(
            v1          /* 이동될 라인 시작점 */
            , v2        /* 이동될 라인 끝점 */
            , v3        /* 기준선 라인 시작점 */
            , v4        /* 기준선 라인 끝점 */
            , 1000.0f   /* 오프셋 */
            );

    // 이동될 라인을 포함한 부재 검색
    List<VIZCore3D.NET.Data.Node> nodes = vizcore3d.Object3D.Find.QuickSearch("BOX2", true);
    if (nodes.Count == 0) return;

    // 부재 이동 및 회전
    nodes[0].TrasformByRawMatrix(matrix);
}


