// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    // 기능 활성화
    vizcore3d.View.KeyboardBasedTransform.Enable = true;

    // 기준 축 지정
    vizcore3d.View.KeyboardBasedTransform.Axis = VIZCore3D.NET.Data.AxisDirection.Z_PLUS;

    // 이동 거리 : 기본, 상세
    vizcore3d.View.KeyboardBasedTransform.Distance = 1000.0f;
    vizcore3d.View.KeyboardBasedTransform.DetailDistance = 100.0f;

    // 회전 각도 : 기본, 상세
    vizcore3d.View.KeyboardBasedTransform.Angle = 90.0f;
    vizcore3d.View.KeyboardBasedTransform.DetailAngle = 10.0f;

    // 이동/회전 이벤트
    vizcore3d.View.KeyboardBasedTransform.OnTransformEvent += KeyboardBasedTransform_OnTransformEvent;
}

private void KeyboardBasedTransform_OnTransformEvent(object sender, Event.EventManager.KeyboardBasedObjectTransformEventArgs e)
{
    // 이동
    if(e.Angle == null)
    {
        VIZCore3D.NET.Data.AxisDirection axis = e.Axis;
        VIZCore3D.NET.Data.Vector3D distance = e.Distance;
        List<VIZCore3D.NET.Data.Node> node = e.Node;
    }
    // 회전
    else
    {
        VIZCore3D.NET.Data.AxisDirection axis = e.Axis;
        VIZCore3D.NET.Data.Vector3D angle = e.Angle;
        List<VIZCore3D.NET.Data.Node> node = e.Node;
    }
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.BeginUpdate();

    // 적용 대상 검색
    List<Data.Node> items = vizcore3d.Object3D.Find.QuickSearch(
        new List<string>() { "GROUND" }, false, true, false, false, true, false);

    if (items.Count == 0) return;

    // 옵션
    vizcore3d.ShapeDrawing.DepthTest = true;

    // Material을 적용할 커스텀 객체 생성
    List<int> customBox = new List<int>();
    foreach (Data.Node item in items)
    {
        VIZCore3D.NET.Data.BoundBox3D boundbox =
                 vizcore3d.Object3D.GeometryProperty.FromIndex(item.Index).GetBoundBox();

        int id = vizcore3d.ShapeDrawing.AddBox(
                      boundbox
                      , 1
                      , System.Drawing.Color.Gray
                      , true
                      );

        customBox.Add(id);
    }

    // Material 등록
    string image = "D:\\Ground-Texture.jpg";
    int MaterialID = vizcore3d.Object3D.Material.Add(image);

    // 커스텀 객체에 Material 설정
    foreach (int item in customBox)
    {
        vizcore3d.ShapeDrawing.SetMaterial(item, MaterialID);
    }

    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.BeginUpdate();

    Data.BoundBox3D boundbox = vizcore3d.Model.BoundBox;

    // SEA(바다) 커스텀 개체 추가
    int customSeaId = vizcore3d.ShapeDrawing.AddSea(
        boundbox.MinZ - 10.0f   /* 높이 : Z축 */
        , true                  /* Visible */
        );

    // 기본 리소스 (SEA Material) 복사
    int material = vizcore3d.Object3D.Material.Copy(Data.ResourceMaterials.SEA);
    System.Drawing.Color color = System.Drawing.Color.FromArgb(127, 255, 255, 255);

    // 리소스 색상 변경
    vizcore3d.Object3D.Material.SetColor(material, color);

    // 수정된 리소스 (Material) 를 기존 커스텀 개체에 지정 
    vizcore3d.ShapeDrawing.SetMaterial(customSeaId, material);

    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    vizcore3d.BeginUpdate();

    vizcore3d.ShapeDrawing.DepthTest = true;

    int customObjectId =
        vizcore3d.ShapeDrawing.AddSea(
        vizcore3d.Model.BoundBox.MinZ   /* Sea Level Height */
        , true                          /* Visible */
        );

    int materialId =
        vizcore3d.Object3D.Material.Copy(
            VIZCore3D.NET.Data.ResourceMaterials.SEA
            );

    System.Drawing.Color color = 
        System.Drawing.Color.FromArgb(127, 255, 255, 255);

    vizcore3d.Object3D.Material.SetColor(
        materialId  /* Material ID */
        , color     /* Meterial Color */
        );

    vizcore3d.ShapeDrawing.SetMaterial(
        customObjectId  /* Custom Object ID */
        , materialId    /* Meterial ID */
        );

    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    List<int> bodies = new List<int>();
    List<int> coordListNum = new List<int>();
    List<VIZCore3D.NET.Data.Vertex3D> v = new List<VIZCore3D.NET.Data.Vertex3D>();
    List<VIZCore3D.NET.Data.Vector3D> n = new List<VIZCore3D.NET.Data.Vector3D>();
    List<float> color = new List<float>();

    // 면 #1
    bodies.Add(2296);     // Body Index
    coordListNum.Add(18); // Vertex Float 총 개수

    // Vertex 좌표 => Triangle Mesh 를 기반
    v.Add(new VIZCore3D.NET.Data.Vertex3D(80180.00, 10600.00, 35572.12));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(71890.00, 10600.00, 35572.12));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(72324.89, 7170.00, 35645.86));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(72324.89, 7170.00, 35645.86));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(80180.00, 7170.00, 35645.86));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(80180.00, 10600.00, 35572.12));

    // Normal 좌표
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));

    // 면의 색상 설정
    color.Add(1.000f); // R
    color.Add(1.000f); // G
    color.Add(0.000f); // B
    color.Add(1);      // A 

    // 면 #2
    bodies.Add(2293);     // Body Index
    coordListNum.Add(18); // Vertex Float 총 개수

    v.Add(new VIZCore3D.NET.Data.Vertex3D(80180.00, 7170.00, 35645.86));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(72324.89, 7170.00, 35645.86));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(71890.00, 3740.00, 35719.60));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(71890.00, 3740.00, 35719.60));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(80180.00, 3740.00, 35719.60));
    v.Add(new VIZCore3D.NET.Data.Vertex3D(80180.00, 7170.00, 35645.86));

    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));
    n.Add(new VIZCore3D.NET.Data.Vector3D(0.00, 0.00, -1.00));

    color.Add(0.043f); // R
    color.Add(0.043f); // G
    color.Add(0.043f); // B
    color.Add(1);      // A

    vizcore3d.MeshEdit.ChangeBodyMesh(
        bodies          /* BODY INDEX LIST */
        , v             /* VERTEX LIST */
        , n             /* NORMAL LIST */
        , color         /* COLOR */
        , coordListNum  /* VERTEX COUNT LIST */
        );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.BeginUpdate();
    List<int> bodies = new List<int>();
    bodies.Add(1234);    

    for( int j = 0; j < bodies.Count; j++)
    {
        int bodyIndex = bodies[j];
        //Get Vertices Info
        List<VIZCore3D.NET.Data.BodyVertexData> vertexData = vizcore3d.MeshEdit.GetBodyVertices(bodyIndex);

        // Vertex Position Move
        for(int i = 0; i < vertexData.Count; i++)
        {
            vertexData[i].Vertex.X += 1000;
        }

        vizcore3d.MeshEdit.SetBodyVertices(
            bodyIndex /* body index */
            , vertexData /* Vertex Info */
            , false);  /* RebuildData */
    }

    vizcore3d.Structure.RebuildData();
    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.BeginUpdate();
    List<int> bodies = new List<int>();
    bodies.Add(1234);    

    for( int j = 0; j < bodies.Count; j++)
    {
        int bodyIndex = bodies[j];
        //Get Vertices Info
        List<VIZCore3D.NET.Data.BodyVertexData> safeData = vizcore3d.MeshEdit.GetBodyVertices(bodyIndex);

        // Vertex Postition Move
        for(int i = 0; i < safeData.Count; i++)
        {
            safeData[i].Vertex.X += 1000;
        }

        vizcore3d.MeshEdit.SetBodyVertices(
            bodyIndex /* body index */
            , safeData /* Vertex Info */
            , false);  /* RebuildData */
    }

    vizcore3d.Structure.RebuildData();
    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    int id = vizcore3d.MeshEdit.GetMaxId();
    int parent = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP)[0].ID;

    List<VIZCore3D.NET.Data.NewNodeItem> items = new List<VIZCore3D.NET.Data.NewNodeItem>();

    for (int i = 0; i < 100; i++)
    {
        VIZCore3D.NET.Data.NewNodeItem item = new VIZCore3D.NET.Data.NewNodeItem();

        item.Parent = parent;
        item.ID = ++id;
        item.Kind = Data.NodeKind.PART;
        item.Name = string.Format("NAME_{0}", item.ID);

        items.Add(item);
    }

    vizcore3d.MeshEdit.CreateNodes(items);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    int id = vizcore3d.MeshEdit.GetMaxId();
    int parent = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP)[0].ID;

    List<VIZCore3D.NET.Data.NewNodeItem> items = new List<VIZCore3D.NET.Data.NewNodeItem>();

    for (int i = 0; i < 100; i++)
    {
        VIZCore3D.NET.Data.NewNodeItem item = new VIZCore3D.NET.Data.NewNodeItem();

        item.Parent = parent;
        item.ID = ++id;
        item.Kind = Data.NodeKind.PART;
        item.Name = string.Format("NAME_{0}", item.ID);

        items.Add(item);
    }

    vizcore3d.MeshEdit.CreateNodes(items, false);

    vizcore3d.Structure.RebuildData();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Parent Node's Index
    int parentIndex = 0;

    // Node Name
    string name = string.Format(
        "NODE #{0}"
        , vizcore3d.Object3D.GetNodeCount()
        );

    // Create Node
    VIZCore3D.NET.Data.Node node = 
        vizcore3d.MeshEdit.CreateNode(
            parentIndex     /* Parent Node's Index */
            , Data.NodeKind.ASSEMBLY /* Node Kind : Assembly, Part */
            , name          /* Node Name */
            );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node assembly = vizcore3d.MeshEdit.CreateNode(0, VIZCore3D.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node part = vizcore3d.MeshEdit.CreateNode(assembly.Index, VIZCore3D.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3d.Object3D.GetNodeCount());        

    int id = vizcore3d.MeshEdit.CreateBody(part.Index, bodyName);

    if (id == -1)
        MessageBox.Show("NG", "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
    else
        MessageBox.Show(string.Format("Body ID : {0}", id), "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Information);
}


