    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(53, 60, 69));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(62, 70, 71));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(62, 63, 72));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(63, 64, 73));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(64, 65, 74));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(65, 66, 75));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(66, 67, 76));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(67, 68, 77));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(54, 78, 55));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(61, 62, 70));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(68, 69, 79));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(62, 71, 72));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(63, 72, 73));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(64, 73, 74));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(65, 74, 75));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(66, 75, 76));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(67, 76, 77));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(68, 77, 79));
    triangleIndex.Add(new VIZCore3D.NET.Data.TriangleIndex(70, 71, 80));            

    vizcore3d.BeginUpdate();

    int rootIndex = vizcore3d.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3D.NET.Data.Node assemblyNode =
        vizcore3d.Structure.CreateNode(
            rootIndex                               /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3D.NET.Data.Node partNode =
        vizcore3d.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    vizcore3d.MeshEdit.CreateBodyWithIndexedTriangleSet(
        partNode.Index
        , "BODY"
        , vertex
        , triangleIndex
        , Color.Yellow
        );

    vizcore3d.EndUpdate();

    vizcore3d.View.ResetView();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // New Empty Model
    int index = vizcore3d.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3D.NET.Data.Node assemblyNode =
        vizcore3d.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3D.NET.Data.Node partNode =
        vizcore3d.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";

    SizeF bottom = new SizeF(30, 40);
    SizeF top = new SizeF(10, 20);
    SizeF offset = new SizeF(2.5f, 4.5f);

    float height = 50;

    VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
    matrix.Identity();
    //matrix.SetRotateX(vizcore3d.View.DegreesToRadians(45.0f));
    //matrix.SetTranslate(1000, 0, 0, false);

    int bodyId = vizcore3d.MeshEdit.CreateBodyPrimitivePyramid(
            partNode.Index      /* Parent Node Index */
            , bodyName          /* Node Name */
            , bottom            /* Bottom Of Size */
            , top               /* Top Of Size */
            , offset            /* Offset Of Size */
            , height            /* Height */
            , matrix            /* Matrix */
            , Color.Green       /* Color */
            , 1                 /* System Type */
            );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // New Empty Model
    int index = vizcore3d.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3D.NET.Data.Node assemblyNode =
        vizcore3d.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3D.NET.Data.Node partNode =
        vizcore3d.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3d.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3d.Object3D.GetBodyIndex(bodyId);

    {
        SizeF bottom = new SizeF(30, 40);
        SizeF top = new SizeF(10, 20);
        SizeF offset = new SizeF(2.5f, 4.5f);

        float height = 50;

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        m.Identity();
        //m.SetRotateX(vizcore3d.View.DegreesToRadians(45.0f));
        //m.SetTranslate(1000, 0, 0, false);

        vizcore3d.MeshEdit.AddPrimitivePyramid(
            bodyIndex           /* Body Node Index */
            , bottom            /* Bottom Of Size */
            , top               /* Top Of Size */
            , offset            /* Offset Of Size */
            , height            /* Height */
            , matrix            /* Matrix */
            , Color.Green       /* Color */
            , false             /* Rebuild Data */
            , 1                 /* System Type */
            );
    }

    vizcore3d.Structure.RebuildData();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node assembly = vizcore3d.MeshEdit.CreateNode(0, VIZCore3D.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node part = vizcore3d.MeshEdit.CreateNode(assembly.Index, VIZCore3D.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3d.Object3D.GetNodeCount());

    List<float> length = new List<float>() { 1000.0f, 2000.0f, 3000.0f };

    VIZCore3D.NET.Data.Matrix3D m = new VIZCore3D.NET.Data.Matrix3D();
    m.Identity();
    //m.SetRotateX(vizcore3d.View.DegreesToRadians(45.0f));
    //m.SetTranslate(1000, 0, 0, false);

    vizcore3d.BeginUpdate();

    int id = vizcore3d.MeshEdit.CreateBodyPrimitiveBox(
        part.Index          /* Parent (Part) Node Index */
        , bodyName          /* Name */
        , length            /* Axis. Length */
        , m                 /* Matrix */
        , Color.Orange      /* Color */
        );

    vizcore3d.EndUpdate();

    if (id == -1)
    {
        MessageBox.Show(
            "NG"
            , "VIZCore3D.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Error
            );
    }
    else
    {
        MessageBox.Show(
            string.Format("Body ID : {0}", id)
            , "VIZCore3D.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Information
            );
    }
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // New Empty Model
    int index = vizcore3d.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3D.NET.Data.Node assemblyNode =
        vizcore3d.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3D.NET.Data.Node partNode =
        vizcore3d.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3d.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3d.Object3D.GetBodyIndex(bodyId);

    {
        List<float> length = new List<float>() { 1000.0f, 2000.0f, 3000.0f };

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        matrix.Identity();

        vizcore3d.MeshEdit.AddPrimitiveBox(
            bodyIndex           /* Body Node Index */
            , length            /* Length */
            , matrix            /* Matrix */
            , Color.Orange      /* Color */
            , false             /* Rebuild Data */
            , 1.0f              /* Scale */
            );
    }

    {
        /* Radius : Top[0], Bottom[1] */
        List<float> radius = new List<float>() { 1000.0f, 1000.0f };

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        matrix.Identity();
        matrix.SetTranslate(1000, 2000, 3000);

        vizcore3d.MeshEdit.AddPrimitiveCone(
            bodyIndex           /* Body Node Index */
            , radius            /* Radius */
            , 2000.0f           /* Height */
            , matrix            /* Matrix */
            , Color.Blue        /* Color */
            , 12                /* Side Count */
            , 1.0f              /* Scale */
            , false             /* Rebuild Data */
            , 1                 /* System Type */
            );
    }

    vizcore3d.Structure.RebuildData();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

public void Example()
{
    // New Empty Model
    int index = vizcore3d.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3D.NET.Data.Node assemblyNode =
        vizcore3d.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3D.NET.Data.Node partNode =
        vizcore3d.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";

    {
        List<float> radius = new List<float>();
        radius.Add(10); // Inside Radius
        radius.Add(20); // Outside Radius

        float height = 15;
        float angle = 90; // Degree

        int numSection = 4;
        //int numSection = Convert.ToInt32(angle / 10);
        //int numSection = Convert.ToInt32(angle / 15);
        //int numSection = Convert.ToInt32(angle);

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        matrix.Identity();
        //matrix.SetRotateX(vizcore3d.View.DegreesToRadians(45.0f));
        //matrix.SetTranslate(1000, 0, 0, false);

        vizcore3d.MeshEdit.CreateBodyPrimitiveRectangularTorus(
            partNode.Index      /* Parent Node Index */
            , bodyName          /* Name */
            , radius            /* Radius */
            , height            /* Height */
            , angle             /* Angle */
            , matrix            /* Matrix */
            , Color.Green       /* Color */
            , numSection        /* Number Of Section */
            , 4                 /* Number of Side (4) */
            , 1.0f              /* Unit Scale (1.0f) */
            , 1                 /* Detail Level (1) */
            );
    }
}


