    VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
    matrix.Identity();
    //matrix.SetRotateX(vizcore3d.View.DegreesToRadians(45.0f));
    //matrix.SetTranslate(1000, 0, 0, false);

