private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    // Delete Items
    int[] items = new int[] { 4, 5, 6, 10};

    foreach (int item in items)
    {
        if (vizcore3d.Metadata.Exist(item) == false) continue;

        vizcore3d.Metadata.Delete(item);
    }
}


