// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

// Mouse Position 마우스 위치
public System.Drawing.Point MousePt { get; set; }

private void Example()
{
    vizcore3d.View.MouseBasedObjectMove.AddMove(5 /* nodeIndex */);
    vizcore3d.View.MouseBasedObjectMove.AddMove(7 /* nodeIndex */);
    vizcore3d.View.MouseBasedObjectMove.AddMove(9 /* nodeIndex */);

    vizcore3d.View.MouseBasedObjectMove.UpdateMoveBase(MousePt.X, MousePt.Y);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void ExampleAuto()
{
    vizcore3d.View.Ocean.Enable = true;
    vizcore3d.View.Ocean.AutoHeight = true;
}

private void Example()
{
    vizcore3d.View.Ocean.Enable = true;
    vizcore3d.View.Ocean.AutoHeight = false;

    VIZCore3D.NET.Data.BoundBox3D boundBox = vizcore3d.Model.BoundBox;

    vizcore3d.View.Ocean.Height = boundBox.MinZ + 10000.0f;
}


