// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example1()
{
    vizcore3d.BeginUpdate();
    for (int i = 0; i < 360; i += 10)
    {
        float fAngleX = i;
        float fAngleY = 2.0f;

        string text = string.Format("{0}", i);

        Color color = Color.FromArgb(255, 0, 255, 0);
        vizcore3d.Panorama.CustomObject.AddText(text, Data.FontSizeKind.SIZE12, color, fAngleX, fAngleY);
     }
     vizcore3d.EndUpdate();
}

private void Example2()
{
    vizcore3d.BeginUpdate();
    for (int i = -80; i < 80; i += 10)
    {
        float fAngleX = 2.0f;
        float fAngleY = i;

        string text = string.Format("{0}", i);

        Color color = Color.FromArgb(255, 0, 0, 255);
        vizcore3d.Panorama.CustomObject.AddText(text, Data.FontSizeKind.SIZE12, color, fAngleX, fAngleY);
     }
     vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example1()
{
    List<Data.Vertex3D> position1 = new List<Data.Vertex3D>();
    for (int i = 0; i <= 360; i += 10)
    {
        float fAngleX = i;
        float fAngleY = 0;

        position1.Add(new Data.Vertex3D(fAngleX, fAngleY, 0.0f));
    }
    vizcore3d.Panorama.CustomObject.AddPolyLine(position1, Color.Red, 1.0f);
}

private void Example2()
{
    List<Data.Vertex3D> position1 = new List<Data.Vertex3D>();
     for (int i = -90; i <= 90; i++)
    {
        float fAngleX = 0;
        float fAngleY = i;

        position1.Add(new Data.Vertex3D(fAngleX, fAngleY, 0.0f));
    }
    Color color = Color.FromArgb(255, 255, 128, 0);
    vizcore3d.Panorama.CustomObject.AddPolyLine(position1, color, 1.0f);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Color Index : rev2bst.config.rev 파일 참고

    VIZCore3D.NET.Manager.PrimitiveObject root =
        vizcore3d.Primitive.AddNode("Root", 4 /* Yellow Color*/);

    VIZCore3D.NET.Manager.PrimitiveObject child1 =
        root.AddNode("CHILD1", 7 /* Blue Color */);

    {
        VIZCore3D.NET.Manager.PrimitiveBox box1 = 
            new VIZCore3D.NET.Manager.PrimitiveBox();

        box1.Set2Point(
            new float[] { 0, 0, 0 }
            , new float[] { 100, 100, 100 }
            , 50.0f
            );

        child1.AddPrimitive(box1);


        VIZCore3D.NET.Manager.PrimitiveBox box2 =
            new VIZCore3D.NET.Manager.PrimitiveBox();

        VIZCore3D.NET.Data.BoundBox3D boundBox1 =
            new VIZCore3D.NET.Data.BoundBox3D(
                new VIZCore3D.NET.Data.Vertex3D(
                    200, 200, 200
                    )
                , new VIZCore3D.NET.Data.Vertex3D(
                    300, 300, 300
                    )
                );

        box2.SetMinMaxPoints(boundBox1);

        child1.AddPrimitive(box2);
    }


    VIZCore3D.NET.Manager.PrimitiveObject child2 =
        root.AddNode("CHILD2", 11 /* Pink Color */);

    {
        VIZCore3D.NET.Manager.PrimitiveCylinder cylinder1 =
            new VIZCore3D.NET.Manager.PrimitiveCylinder();

        cylinder1.Set2Point(
            new float[] { 400, 400, 400 }
            , new float[] { 600, 600, 600 }
            , 50.0f
            );

        child2.AddPrimitive(cylinder1);
    }

    // Open Model
    vizcore3d.Primitive.OpenModel("Primitive", false);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Color Index : rev2bst.config.rev 파일 참고

    VIZCore3D.NET.Manager.PrimitiveObject root =
        vizcore3d.Primitive.AddNode("Root", 4 /* Yellow Color*/);

    VIZCore3D.NET.Manager.PrimitiveObject child1 =
        root.AddNode("CHILD1", 7 /* Blue Color */);

    {
        VIZCore3D.NET.Manager.PrimitiveBox box1 = 
            new VIZCore3D.NET.Manager.PrimitiveBox();

        box1.Set2Point(
            new float[] { 0, 0, 0 }
            , new float[] { 100, 100, 100 }
            , 50.0f
            );

        child1.AddPrimitive(box1);


        VIZCore3D.NET.Manager.PrimitiveBox box2 =
            new VIZCore3D.NET.Manager.PrimitiveBox();

        VIZCore3D.NET.Data.BoundBox3D boundBox1 =
            new VIZCore3D.NET.Data.BoundBox3D(
                new VIZCore3D.NET.Data.Vertex3D(
                    200, 200, 200
                    )
                , new VIZCore3D.NET.Data.Vertex3D(
                    300, 300, 300
                    )
                );

        box2.SetMinMaxPoints(boundBox1);

        child1.AddPrimitive(box2);
    }


    VIZCore3D.NET.Manager.PrimitiveObject child2 =
        root.AddNode("CHILD2", 11 /* Pink Color */);

    {
        VIZCore3D.NET.Manager.PrimitiveCylinder cylinder1 =
            new VIZCore3D.NET.Manager.PrimitiveCylinder();

        cylinder1.Set2Point(
            new float[] { 400, 400, 400 }
            , new float[] { 600, 600, 600 }
            , 50.0f
            );

        child2.AddPrimitive(cylinder1);
    }

    // Open Model
    vizcore3d.Primitive.OpenModel("Primitive", false);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    vizcore3d.View.ScreenAxisRotation.LockZAxis = false;
    vizcore3d.View.ScreenAxisRotation.BasedZAxis = VIZCore3D.NET.Data.ZAxisRotationKind.Axis;
    vizcore3d.View.ScreenAxisRotation.AngleLarge = 90.0f;
    vizcore3d.View.ScreenAxisRotation.AngleSmall = 10.0f;
    vizcore3d.View.ScreenAxisRotation.Enable = true;
}


