    System.Drawing.Point p2 = new System.Drawing.Point(610765, -292509);

    int shapeId = vizcore3d.ShapeDrawing.DrawRectangle(
        p1              /* Min. Point  */
        , p2            /* Max. Point */
        , 91127         /* Height : Z Axis. */
        , 1             /* Group Id */
        , Color.Red     /* Line Color */
        , 5             /* Thickness */
        , true          /* Visible */
        );
}


