// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // 모델 색상 변경 후, 변경된 색상 조회
    vizcore3d.Object3D.Color.OnColorChangedEvent += Color_OnColorChangedEvent;

    // 개체 색상 변경 다이얼로그 실행
    // 혹은 사용자가 모델트리의 Context Menu에서 색상 변경 수행
    vizcore3d.Object3D.Color.ChangeColorDialog();
}

private void Color_OnColorChangedEvent(object sender, Event.EventManager.CustomColorEventArgs e)
{
    StringBuilder sb = new StringBuilder();
    sb.AppendFormat("Event : {0}\n", "OnColorChangedEvent");
    sb.AppendFormat("Node : {0}\n", e.Objects.Count);
    sb.AppendFormat("Color - R:{0}/G:{1}/B:{1}\n", e.CustomColor.R, e.CustomColor.G, e.CustomColor.B);
    System.Diagnostics.Trace.WriteLine(sb.ToString());
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.BeginUpdate();

    List<VIZCore3D.NET.Data.Node> node =
        vizcore3d.Object3D.Find.QuickSearch(
            new List<string>() { "PIPE_*", "EQUIP_*", "HULL_*" }
            , false
            , true
            , false
            , false
            , false
            , false
            );

    vizcore3d.Object3D.Color.SetDefaultColor(node, Color.Blue);

    vizcore3d.EndUpdate();

    // Some code here...
    {
        // Set Color
    }

    // Restore
    vizcore3d.Object3D.Color.RestoreColorAll();
    vizcore3d.Object3D.Color.RestoreAll();

    // Clear
    vizcore3d.Object3D.Color.ClearDefaultColor();
}


