// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Frame.HasFrame == true) return;

    string file = "C:\\Frame\\SHIP.dmp";
    vizcore3d.Frame.Open(file);

    if (vizcore3d.Frame.Visible == false)
        vizcore3d.Frame.Visible = true;
}

private void ExampleUri()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Frame.HasFrame == true) return;

    string uri = "http://server/SHIP.dmp";
    vizcore3d.Frame.OpenUri(uri);

    if (vizcore3d.Frame.Visible == false)
        vizcore3d.Frame.Visible = true;
}

private void ExampleStream()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Frame.HasFrame == true) return;

    string uri = "http://server/SHIP.dmp";
    System.Net.WebClient client = new System.Net.WebClient();
    byte[] buffer = client.DownloadData(uri);
    vizcore3d.Frame.OpenStream(buffer);

    if (vizcore3d.Frame.Visible == false)
        vizcore3d.Frame.Visible = true;
}


