private void CreateVIZCore3D()
{
    vizcore3d.OnInitializedVIZCore3D += VIZCore3D_OnInitializedVIZCore3D;
}

private void VIZCore3D_OnInitializedVIZCore3D(object sender, EventArgs e)
{
    VIZCore3D.NET.Data.LicenseResults result = vizcore3d.License.LicenseFile("C:\\Temp\\VIZCore3D.NET.lic"); 
    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }
}


private void CreateVIZCore3D()
{
    vizcore3d.OnInitializedVIZCore3D += VIZCore3D_OnInitializedVIZCore3D;
}

private void VIZCore3D_OnInitializedVIZCore3D(object sender, EventArgs e)
{
    VIZCore3D.NET.Data.LicenseResults result = vizcore3d.License.LicenseServer("127.0.0.1", 8901);
    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }
}


private void CreateVIZCore3D()
{
    vizcore3d.OnInitializedVIZCore3D += VIZCore3D_OnInitializedVIZCore3D;
}

private void VIZCore3D_OnInitializedVIZCore3D(object sender, EventArgs e)
{
    VIZCore3D.NET.Data.LicenseResults result = vizcore3d.License.LicenseServer("127.0.0.1", 8901, Data.Products.AUTO);
    //VIZCore3D.NET.Data.LicenseResults result = vizcore3d.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZZARD_MANAGER);
    //VIZCore3D.NET.Data.LicenseResults result = vizcore3d.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZZARD_STANDARD);
    //VIZCore3D.NET.Data.LicenseResults result = vizcore3d.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZCORE3D_MANAGER);
    //VIZCore3D.NET.Data.LicenseResults result = vizcore3d.License.LicenseServer("127.0.0.1", 8901, Data.Products.VIZCORE3D_STANDARD);

    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(string.Format("LICENSE CODE : {0}", result.ToString()), "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
        return;
    }
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Add Event
    vizcore3d.Review.Measure.OnMeasureIntersectionPointEvent += Measure_OnMeasureIntersectionPointEvent;

    bool repeatPickSurface = false;
    bool customMode = true;

    vizcore3d.Review.Measure.AddIntersectionOfLineAndPlane(
        repeatPickSurface   /* 반복수행모드 */
        , customMode        /* True: 화면에 등록되지 않고, 측정 수행 후 이벤트 발생 / False : 화면에 등록됨 */
        );
}

private void Measure_OnMeasureIntersectionPointEvent(object sender, Event.EventManager.MeasureIntersectionPointEventArgs e)
{
    // 선택한 부재의 인덱스
    int indexLine = e.LineIndex;
    int indexPlane = e.PlaneIndex;

    // 선택 정보
    VIZCore3D.NET.Data.Vertex3D hitPosition = e.HitPosition;
    VIZCore3D.NET.Data.Vertex3D lineDirection = e.LineDirection;
    VIZCore3D.NET.Data.Vertex3D planeNormal = e.PlaneNormal;
}


// VIZCore3D.NET Control
