// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    VIZCore3D.NET.Data.Node pipe = vizcore3d.Object3D.FromIndex(4);

    List<VIZCore3D.NET.Data.Node> cylinder1 = pipe.GetChildObject3d(
        Data.Object3DChildOption.CHILD_ONLY         /* Option */
        , new List<string>() { "Part 1 of PIPE" }   /* Keyword */
        , false     /* Join Condition */
        , false     /* Assembly Only */
        , false     /* Visible Only */
        , false     /* Full Match */
        );

    List<VIZCore3D.NET.Data.Node> cylinder2 = pipe.GetChildObject3d(
        Data.Object3DChildOption.CHILD_ONLY         /* Option */
        , new List<string>() { "Part 2 of PIPE" }   /* Keyword */
        , false     /* Join Condition */
        , false     /* Assembly Only */
        , false     /* Visible Only */
        , false     /* Full Match */
        );

    if (cylinder1.Count == 0) return;
    if (cylinder2.Count == 0) return;

    int measureId = vizcore3d.Review.Measure.AddCustomCylinderCylinderAxisCrossPoint(
        cylinder1[0].Index      /* Cylinder Node Index */
        , cylinder2[0].Index    /* Cylinder Node Index */
        );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    List<Data.Node> nodes = vizcore3d.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP);

    List<int> nodesIdx = new List<int>();
    foreach (Data.Node item in nodes)
        nodesIdx.Add(item.Index);

    Data.BoundBoxParameter param = vizcore3d.Review.Measure.GetBoundBoxByPlaneVertex(
        nodesIdx                        /* Node Index List */
        , new Data.Vector3D(0, 0, 1)    /* Normal */
        );

    // Show Result
    vizcore3d.ShapeDrawing.AddVertex(
        param.VertexItems /* Vertex List  */
        , 0               /* Group Id */
        , Color.Red       /* Color */
        , 3.0f            /* Radius */
        , 3.0f            /* Size */
        , true            /* Visible */
        );
}

private void Example_DrawBox()
{
    List<Data.Node> nodes = vizcore3d.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP);

    List<int> nodesIdx = new List<int>();
    foreach (Data.Node item in nodes)
        nodesIdx.Add(item.Index);

    Data.BoundBoxParameter param = vizcore3d.Review.Measure.GetBoundBoxByPlaneVertex(
        nodesIdx                        /* Node Index List */
        , new Data.Vertex3D(0, 0, 1)    /* Normal */
        );

    vizcore3d.BeginUpdate();

    // Draw Custom Line
    int shapeId = vizcore3d.ShapeDrawing.AddLine(
        param.GetShapeDrawingLineParameter()
        , 0
        , Color.Black
        , 5.0f
        , true
        );

    // Use DepthTest
    vizcore3d.ShapeDrawing.DepthTest = true;

    // Draw Point
    for (int i = 0; i < 8; i++)
    {
        int noteId = vizcore3d.Review.Note.AddNote3D(i.ToString(), param.VertexItems[i]);

        // Set DepthTest
        vizcore3d.Review.Note.EnableDepthTest(noteId, true);
    }

    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    List<Data.Node> nodes = vizcore3d.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP);

    Data.BoundBoxParameter param = vizcore3d.Review.Measure.GetBoundBoxByPlaneVertex(
