// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void ProgressChangedEvent()
{
    vizcore3d.Model.OnModelProgressChangedEvent += Model_OnModelProgressChangedEvent;

    string path = "C:\\Model\\BLOCK.viz";

    vizcore3d.Model.Open(path);
}

private void Model_OnModelProgressChangedEvent(object sender, VIZCore3D.NET.Event.EventManager.ModelProgressEventArgs e)
{
    string Mode = String.Empty;

    switch (e.Mode)
    {
        case VIZCore3D.NET.Data.ModelProgressModes.READ:
            Mode = "파일 읽기 (열기)...";
            break;
        case VIZCore3D.NET.Data.ModelProgressModes.WRITE:
            Mode = "파일 저장 ...";
            break;
        case VIZCore3D.NET.Data.ModelProgressModes.MERGE:
            Mode = "파일 추가 (병합)...";
            break;
        default:
            break;
    }

    System.Diagnostics.Trace.WriteLine(string.Format("{0} - Progress : {1} %", Mode, e.Progress));
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void AddStreamFile(Dictionary<string, byte[]> source)
{
    vizcore3d.Model.OnStreamProgressChangedEvent += Model_OnStreamProgressChangedEvent;

    List<VIZCore3D.NET.Data.StreamData> stream = new List<VIZCore3D.NET.Data.StreamData>();

    foreach (KeyValuePair<string, byte[]> item in source)
    {
        VIZCore3D.NET.Data.StreamData data = new VIZCore3D.NET.Data.StreamData(
            item.Value  /* byte[] */
            , item.Key  /* Model Name */
            );

        stream.Add(data);
    }

    vizcore3d.Model.AddStream(stream);
}

private void Model_OnStreamProgressChangedEvent(object sender, VIZCore3D.NET.Event.EventManager.StreamProgressEventArgs e)
{
    System.Diagnostics.Trace.WriteLine(string.Format("[{0}/{1}] {2} : {3}", e.Index, e.Count, e.ModelName, e.Result));
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    VIZCore3D.NET.Data.BoundBox3D boundBox =
        vizcore3d.Model.BoundBox;
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // New Empty Model
    int index = vizcore3d.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3D.NET.Data.Node assemblyNode =
        vizcore3d.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3D.NET.Data.Node partNode =
        vizcore3d.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3d.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3d.Object3D.GetBodyIndex(bodyId);

    {
        List<float> length = new List<float>() { 1000.0f, 2000.0f, 3000.0f };

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        matrix.Matrix[0] = 1.0f;
        matrix.Matrix[1] = 0.0f;
        matrix.Matrix[2] = 0.0f;

        matrix.Matrix[3] = 0.0f;
        matrix.Matrix[4] = 1.0f;
        matrix.Matrix[5] = 0.0f;

        matrix.Matrix[6] = 0.0f;
        matrix.Matrix[7] = 0.0f;
        matrix.Matrix[8] = 1.0f;

        matrix.Matrix[9] = 0.0f;
        matrix.Matrix[10] = 0.0f;
        matrix.Matrix[11] = 0.0f;

        vizcore3d.MeshEdit.AddPrimitiveBox(
            bodyIndex           /* Body Node Index */
            , length            /* Length */
            , matrix            /* Matrix */
            , Color.Orange      /* Color */
            , 1.0f              /* Scale */
            , false             /* Rebuild Data */
            );
    }

    {
        /* Radius : Top[0], Bottom[1] */
        List<float> radius = new List<float>() { 1000.0f, 1000.0f };

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        matrix.Matrix[0] = 1.0f;
        matrix.Matrix[1] = 0.0f;
        matrix.Matrix[2] = 0.0f;

        matrix.Matrix[3] = 0.0f;
        matrix.Matrix[4] = 1.0f;
        matrix.Matrix[5] = 0.0f;

        matrix.Matrix[6] = 0.0f;
        matrix.Matrix[7] = 0.0f;
        matrix.Matrix[8] = 1.0f;

        matrix.Matrix[9] = 1000.0f;
        matrix.Matrix[10] = 1000.0f;
        matrix.Matrix[11] = 2000.0f;

        vizcore3d.MeshEdit.AddPrimitiveCone(
            bodyIndex           /* Body Node Index */
            , radius            /* Radius */
            , 2000.0f           /* Height */
            , matrix            /* Matrix */
            , Color.Blue        /* Color */
            , 12                /* Side Count */
            , 1.0f              /* Scale */
            , 1                 /* System Type */
            , false             /* Rebuild Data */
            );
    }

    vizcore3d.Structure.RebuildData();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void AddFile(List<string> file, bool closeModel)
{
    if (closeModel == true && vizcore3d.Model.IsOpen() == true)
        vizcore3d.Model.Close();

    vizcore3d.Model.Add(file);
}


