    bool result = vizcore3d.Model.ConvertToVIZW(
        input                                   /* INPUT VIZ File */
        , output                                /* OUTPUT VIZW Path */
        , false                                 /* GENERATE VIZ File */
        , triangleMeshCount                     /* TRIANGLE MESH COUNT - DEFAULT : 1,000,000 */
        , VIZCore3D.NET.Data.LogKind.DETAIL     /* LOG */
        );
}


