// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void StructureFromFile()
{
    string path = "C:\\Model\\BLOCK.viz";

    VIZCore3D.NET.ShdCore.StructureManager stru = 
        VIZCore3D.NET.Manager.ModelManager.GetModelStructure(path);

    List<VIZCore3D.NET.ShdCore.ModelTreeNode> roots = stru.Roots;

    // Structure
    foreach (VIZCore3D.NET.ShdCore.ModelTreeNode root in roots)
    {
        string name = root.NodeName;

        if(root.Nodes.Count != 0)
        {
            NavigateNodes(root);
        }
    }

    // Node List
    foreach (VIZCore3D.NET.ShdCore.ModelTreeNode item in stru.GetStructureNodeList())
    {
        // Some code here...
    }
}

private void NavigateNodes(VIZCore3D.NET.ShdCore.ModelTreeNode parent)
{
    foreach (VIZCore3D.NET.ShdCore.ModelTreeNode item in parent.Nodes)
    {
        string name = item.NodeName;

        if(item.Nodes.Count != 0)
        {
            NavigateNodes(item);
        }
    }
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // INPUT
    OpenFileDialog dlg = new OpenFileDialog();
    dlg.Filter = "VIZ File (*.viz)|*.viz";
    if (dlg.ShowDialog() != DialogResult.OK) return;

    // OUTPUT DIR.
    FolderBrowserDialog output = new FolderBrowserDialog();
    output.SelectedPath = System.IO.Path.GetDirectoryName(dlg.FileName);
    if (output.ShowDialog() != DialogResult.OK) return;

    List<int> id = new List<int>();

    // Load Structure
    VIZCore3D.NET.ShdCore.StructureManager stru =
        new VIZCore3D.NET.ShdCore.StructureManager(dlg.FileName);

    // All Node
    List<VIZCore3D.NET.ShdCore.ModelTreeNode> nodes = stru.GetStructureNodeList();

    // Find Node
    foreach (VIZCore3D.NET.ShdCore.ModelTreeNode node in nodes)
    {
        if (node.NodeName.Contains("PIPE") == false) continue;
        id.Add(Convert.ToInt32(node.EntityId));
    }

    if (id.Count == 0) return;

    // Export
    vizcore3d.Model.ExportNodes(
        dlg.FileName            /* INPUT (VIZ) */
        , output.SelectedPath   /* OUTPUT DIR. */
        , id                    /* NODE ID */
        );
}


