// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Review.Note.Items.Count == 0) return;

    // 겹쳐진 노트 항목 검색
    bool result = vizcore3d.Review.Note.FindOverlappingItems(true);

    // 겹쳐진 노트 항목이 없음
    if (result == false)
    {
        foreach (VIZCore3D.NET.Data.NoteItem item in vizcore3d.Review.Note.Items)
        {
            VIZCore3D.NET.Data.NoteStyle style = vizcore3d.Review.Note.GetStyle(item.ID);
            style.BackgroundColor = Color.Yellow;
            vizcore3d.Review.Note.SetStyle(item.ID, style);
        }

        return;
    }

    vizcore3d.BeginUpdate();

    foreach (VIZCore3D.NET.Data.NoteItem item in vizcore3d.Review.Note.Items)
    {
        VIZCore3D.NET.Data.NoteStyle style = vizcore3d.Review.Note.GetStyle(item.ID);

        // 현재 노트와 겹쳐진 노트가 없음
        if (item.OverlappingItems.Count == 0)
        {
            style.BackgroundColor = Color.Yellow;
        }
        // 현재 노트와 겹쳐진 노트가 있음
        else
        {
            // 겹쳐진 노트의 아이디가 현재보다 클 경우, 
            // 나중에 생성된 노트이고,
            // 현재 노트보다 위에 그려짐.
            if (item.ID < item.OverlappingItems[0])
            {
                style.BackgroundColor = Color.Red;
            }
            else
            {
                style.BackgroundColor = Color.White;
            }
        }

        vizcore3d.Review.Note.SetStyle(item.ID, style);
    }

    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Review.Note.Items.Count == 0) return;

    List<int> excludedNote = new List<int>();
    excludedNote.Add(4);
    excludedNote.Add(5);

    // 겹쳐진 노트 항목 검색
    bool result = vizcore3d.Review.Note.FindOverlappingItems(true, null, excludedNote);

    // 겹쳐진 노트 항목이 없음
    if (result == false)
    {
        foreach (VIZCore3D.NET.Data.NoteItem item in vizcore3d.Review.Note.Items)
        {
            VIZCore3D.NET.Data.NoteStyle style = vizcore3d.Review.Note.GetStyle(item.ID);
            style.BackgroundColor = Color.Yellow;
            vizcore3d.Review.Note.SetStyle(item.ID, style);
        }

        return;
    }

    vizcore3d.BeginUpdate();

    foreach (VIZCore3D.NET.Data.NoteItem item in vizcore3d.Review.Note.Items)
    {
        VIZCore3D.NET.Data.NoteStyle style = vizcore3d.Review.Note.GetStyle(item.ID);

        // 현재 노트와 겹쳐진 노트가 없음
        if (item.OverlappingItems.Count == 0)
        {
            style.BackgroundColor = Color.Yellow;
        }
        // 현재 노트와 겹쳐진 노트가 있음
        else
        {
            // 겹쳐진 노트의 아이디가 현재보다 클 경우, 
            // 나중에 생성된 노트이고,
            // 현재 노트보다 위에 그려짐.
            if (item.ID < item.OverlappingItems[0])
            {
                style.BackgroundColor = Color.Red;
            }
            else
            {
                style.BackgroundColor = Color.White;
            }
        }

        vizcore3d.Review.Note.SetStyle(item.ID, style);
    }

    vizcore3d.EndUpdate();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.Object3D.OnObject3DSelected += Object3D_OnObject3DSelected;
}

private void Object3D_OnObject3DSelected(object sender, Event.EventManager.Object3DSelectedEventArgs e)
{
    if (e.Node.Count == 0) return;

    foreach (VIZCore3D.NET.Data.Node item in e.Node)
    {
        // Some code here...
    }
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.Object3D.OnObject3DSelecting += Object3D_OnObject3DSelecting;
}

private void Object3D_OnObject3DSelecting(object sender, Event.EventManager.Object3DSelectingEventArgs e)
{
    // 선택된 모델 이름에 "PIPE" 이름이 포함된 경우에
    if(e.Node.NodeName.Contains("PIPE") == true)
    {
        // 선택 가능
        e.Cancel = false;
    }
    else
    {
        // 선택 취소
        e.Cancel = true;
    }
}


