// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Event 선언
    vizcore3d.View.OnViewDragDrop += View_OnViewDragDrop;
}

private void View_OnViewDragDrop(object sender, Event.EventManager.ViewDragDropEventArgs e)
{
    int x = e.X;
    int y = e.Y;

    // 해당 위치 개체 조회
    int index = vizcore3d.Object3D.HitTest(x, y);
    if(index != -1)
    {
        VIZCore3D.NET.Data.Node node = vizcore3d.Object3D.FromIndex(index);
    }
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    List<VIZCore3D.NET.Data.Node> items = null;

    // Root
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.ROOT);

    // File Node
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.FILE);

    // All Node
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.ALL);            

    // Leaf Assembly
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.LEAF_ASSEMBLY);

    // Parent Of Leaf Assembly
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.PARENT_LEAF_ASSEMBLY);

    // Part
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.PART);

    // Visible Part
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.VISIBLE_PART);

    // Hidden Node
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.HIDDEN_NODE);

    // Locked Hidden
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.LOCKED_HIDDEN);

    // Selected
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_ALL);

    // Selected All with Body
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_ALL_INCLUDE_BODY);

    // Selected Part
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_PART);

    // Selected Top(Root)
    items = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP);
}


