// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.Object3D.OnObject3DSelected += Object3D_OnObject3DSelected;
}

private void Object3D_OnObject3DSelected(object sender, Event.EventManager.Object3DSelectedEventArgs e)
{
    if (e.Node.Count == 0) return;

    List<int> orderedIndex = vizcore3d.Object3D.GetSelectedIndexInSelectedOrder();
    List<VIZCore3D.NET.Data.Node> orderedNode = vizcore3d.Object3D.GetSelectedNodeInSelectedOrder();

    for (int i = 0; i < orderedIndex.Count; i++)
    {
        System.Diagnostics.Debug.WriteLine(orderedIndex[i]);

        // Or
        System.Diagnostics.Debug.WriteLine(orderedNode[i].NodeName);
    }
}


