private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Search()
{
    List<string> keyword = new List<string>();
    keyword.Add("PIPE_01_*");
    keyword.Add("PIPE_02_*");
    keyword.Add("PIPE_03_*");
    keyword.Add("PIPE_04_*");

    List<VIZCore3D.NET.Data.Node> items =
        vizcore3d.Object3D.Find.QuickSearch(
            keyword
            , false /* Join Condition : True - And, False - Or */
            , true  /* True : Assembly Only, False : Assembly + Part */
            , false /* True : Visible Only, False : All Node */
            , false /* True : Selected Object, False : All Node */
            , false /* True : Full Match, False : Contains */
            , false /* True : Include Node Path, False : Except Node Path */
            );

    if (vizcore3d.View.XRay.Enable == false)
        vizcore3d.View.XRay.Enable = true;

    vizcore3d.View.XRay.SelectionObject3DType = VIZCore3D.NET.Data.SelectionObject3DTypes.OPAQUE_OBJECT3D;
    vizcore3d.View.XRay.ColorType = VIZCore3D.NET.Data.XRayColorTypes.OBJECT_COLOR;

    vizcore3d.View.XRay.Select(
        items   /* Node */
        , true  /* Selection */
        , true  /* Set Rotation Pivot */
        );
}


private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void ExportOutside()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    List<VIZCore3D.NET.Data.Node> items =
        vizcore3d.Object3D.Find.GetOutsidePart(true);

    string path = "C:\\Model\\Outside.viz";

    bool result = vizcore3d.Model.ExportVIZ(path, items);

    if (result == false) return;

    VIZCore3D.NET.Utility.ExplorerHelper.Show(path);
}


private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void ExportOutside()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    List<VIZCore3D.NET.Data.Node> items =
        vizcore3d.Object3D.Find.GetOutsideLeafAssembly();

    string path = "C:\\Model\\Outside.viz";

    bool result = vizcore3d.Model.ExportVIZ(path, items);

    if (result == false) return;

    VIZCore3D.NET.Utility.ExplorerHelper.Show(path);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    Dictionary<string, List<VIZCore3D.NET.Data.Node>> items
        = vizcore3d.Object3D.Find.GetFunctionGroup(
            "898C4327-7A21-464F-BAC8-C0B731351DEE"   /* HHI MBOM 2020 */
            );

    // Case 1)
    foreach (KeyValuePair<string, List<VIZCore3D.NET.Data.Node>> item in items)
    {
        // Some code here...
    }

    // Case 2)
    List<VIZCore3D.NET.Data.Node> MACH_PIPE 
        = items.ContainsKey("MACH_PIPE") == true 
        ? items["MACH_PIPE"] 
        : new List<VIZCore3D.NET.Data.Node>();

    List<VIZCore3D.NET.Data.Node> HOFT_PIPE
        = items.ContainsKey("HOFT_PIPE") == true
        ? items["HOFT_PIPE"]
        : new List<VIZCore3D.NET.Data.Node>();

    vizcore3d.BeginUpdate();

    vizcore3d.View.XRay.Enable = true;
    vizcore3d.View.XRay.Clear();

    vizcore3d.View.XRay.Select(MACH_PIPE, true);
    vizcore3d.View.XRay.Select(HOFT_PIPE, true);

    vizcore3d.EndUpdate();
}


private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void AddSelectionBox()
{
    string title = "Box #1";

    int id = vizcore3d.SelectionBox.Add(
        new VIZCore3D.NET.Data.Vertex3D(0, 0, 0)
        , new VIZCore3D.NET.Data.Vertex3D(100, 100, 100)
        , Color.White
        , Color.Black
        , title
        );

    List<int> objects = 
        vizcore3d.SelectionBox.GetObject3DIndex(
            id
            , VIZCore3D.NET.Data.BoundBoxSearchOption.IncludingPart
            );
}


