// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // New Empty Model
    int index = vizcore3d.Model.NewEmptyModel("MODEL");

    string assemblyName = "ASSEMBLY";
    VIZCore3D.NET.Data.Node assemblyNode =
        vizcore3d.Structure.CreateNode(
            index                                   /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.ASSEMBLY  /* Node Kind */
            , assemblyName                          /* Node Name */
            );

    string partName = "PART";
    VIZCore3D.NET.Data.Node partNode =
        vizcore3d.Structure.CreateNode(
            assemblyNode.Index                      /* Parent Node Index */
            , VIZCore3D.NET.Data.NodeKind.PART      /* Node Kind */
            , partName                              /* Node Name */
            );

    string bodyName = "BODY";
    int bodyId =
        vizcore3d.Structure.CreateBody(
            partNode.Index                          /* Parent Node Index */
            , bodyName                              /* Node Name */
            );

    int bodyIndex = vizcore3d.Structure.GetBodyIndex(bodyId);

    {
        List<float> length = new List<float>() { 1000.0f, 2000.0f, 3000.0f };

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        matrix.Identity();
        //matrix.SetRotateX(vizcore3d.View.DegreesToRadians(45.0f));
        //matrix.SetTranslate(1000, 2000, 3000);

        vizcore3d.MeshEdit.AddPrimitiveBox(
            bodyIndex           /* Body Node Index */
            , length            /* Length */
            , matrix            /* Matrix */
            , Color.Orange      /* Color */
            , 1.0f              /* Scale */
            , false             /* Rebuild Data */
            );
    }

    {
        /* Radius : Top[0], Bottom[1] */
        List<float> radius = new List<float>() { 1000.0f, 1000.0f };

        VIZCore3D.NET.Data.Matrix3D matrix = new VIZCore3D.NET.Data.Matrix3D();
        matrix.Identity();
        //matrix.SetRotateX(vizcore3d.View.DegreesToRadians(45.0f));
        matrix.SetTranslate(1000, 2000, 3000);

        vizcore3d.MeshEdit.AddPrimitiveCone(
            bodyIndex           /* Body Node Index */
            , radius            /* Radius */
            , 2000.0f           /* Height */
            , matrix            /* Matrix */
            , Color.Blue        /* Color */
            , 12                /* Side Count */
            , 1.0f              /* Scale */
            , 1                 /* System Type */
            , false             /* Rebuild Data */
            );
    }

    vizcore3d.Structure.RebuildData();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    List<VIZCore3D.NET.Data.Node> selectedItems =
        vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_PART);

    Dictionary<int, int> items = vizcore3d.Structure.CopyNodes(selectedItems);

    if (items.Count == 0)
    {
        MessageBox.Show("Node is null.", "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Warning);
        return;
    }

    foreach (KeyValuePair<int, int> item in items)
    {
        VIZCore3D.NET.Data.Node source = vizcore3d.Object3D.FromIndex(item.Key);
        VIZCore3D.NET.Data.Node target = vizcore3d.Object3D.FromIndex(item.Value);

        MessageBox.Show(
            string.Format(
                "{0} - {1}, {2} / {3}"
            , item.Key
            , item.Value
            , source.NodeName
            , target.NodeName
            )
            , "VIZCore3D.NET"
            , MessageBoxButtons.OK
            , MessageBoxIcon.Information
            );
    }
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Parent Node's Index
    int parentIndex = 0;

    // Node Name
    string name = string.Format(
        "NODE #{0}"
        , vizcore3d.Object3D.GetNodeCount()
        );

    // Create Node
    VIZCore3D.NET.Data.Node node = 
        vizcore3d.Structure.CreateNode(
            parentIndex                 /* Parent Node's Index */
            , Data.NodeKind.ASSEMBLY    /* Node Kind : Assembly, Part */
            , name                      /* Node Name */
            );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node assembly = vizcore3d.Structure.CreateNode(0, VIZCore3D.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node part = vizcore3d.Structure.CreateNode(assembly.Index, VIZCore3D.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3d.Object3D.GetNodeCount());        

    int id = vizcore3d.Structure.CreateBody(part.Index, bodyName);

    if (id == -1)
        MessageBox.Show("NG", "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
    else
        MessageBox.Show(string.Format("Body ID : {0}", id), "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Information);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    string assemblyName = string.Format("Assembly #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node assembly = vizcore3d.Structure.CreateNode(0, VIZCore3D.NET.Data.NodeKind.ASSEMBLY, assemblyName);
    if (assembly == null) return;

    string partName = string.Format("Part #{0}", vizcore3d.Object3D.GetNodeCount());
    VIZCore3D.NET.Data.Node part = vizcore3d.Structure.CreateNode(assembly.Index, VIZCore3D.NET.Data.NodeKind.PART, partName);
    if (part == null) return;

    string bodyName = string.Format("Body #{0}", vizcore3d.Object3D.GetNodeCount());        

    int bodyIndex = -1;
    int bodyId = vizcore3d.Structure.CreateBody(part.Index, bodyName, out bodyIndex);

    if (bodyId == -1)
        MessageBox.Show("NG", "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Error);
    else
        MessageBox.Show(string.Format("Body ID : {0}", bodyId), "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Information);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example1()
{
    int id = vizcore3d.Structure.GetMaxId();
    int parent = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP)[0].ID;

    List<VIZCore3D.NET.Data.NewNodeItem> items = new List<VIZCore3D.NET.Data.NewNodeItem>();

    for (int i = 0; i < 100; i++)
    {
        VIZCore3D.NET.Data.NewNodeItem item = new VIZCore3D.NET.Data.NewNodeItem();

        item.ParentId = parent;
        item.ID = ++id;
        item.Kind = Data.NodeKind.PART;
        item.Name = string.Format("NAME_{0}", item.ID);

        items.Add(item);
    }

    vizcore3d.Structure.CreateNodes(items);
}

private void Example2()
{
    int id = vizcore3d.Structure.GetMaxId();
    int parent = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP)[0].ID;

    List<VIZCore3D.NET.Data.NewNodeItem> items = new List<VIZCore3D.NET.Data.NewNodeItem>();

    for (int i = 0; i < 100; i++)
    {
        VIZCore3D.NET.Data.NewNodeItem assy = new VIZCore3D.NET.Data.NewNodeItem();

        int assyId = ++id;

        assy.ParentId = parent;
        assy.ID = assyId;
        assy.Kind = Data.NodeKind.ASSEMBLY;
        assy.Name = string.Format("NAME_ASSY_{0}", assy.ID);

        items.Add(assy);

        {
            VIZCore3D.NET.Data.NewNodeItem part = new VIZCore3D.NET.Data.NewNodeItem();

            int partId = ++id;

            part.ParentId = assyId;
            part.ID = partId;
            part.Kind = Data.NodeKind.PART;
            part.Name = string.Format("NAME_PART_{0}", part.ID);

            items.Add(part);

            {
                VIZCore3D.NET.Data.NewNodeItem body = new VIZCore3D.NET.Data.NewNodeItem();

                int bodyId = ++id;

                body.ParentId = partId;
                body.ID = bodyId;
                body.Kind = Data.NodeKind.BODY;
                body.Name = string.Format("NAME_BODY_{0}", body.ID);

                items.Add(body);
            }
        }
    }

    vizcore3d.Structure.CreateNodes(items);
}


