// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.TextDrawing.OnTextDrawingItemClickedEvent += TextDrawing_OnTextDrawingItemClickedEvent;
}

private void TextDrawing_OnTextDrawingItemClickedEvent(object sender, Event.EventManager.TextDrawingItemClickedEventArgs e)
{
    StringBuilder sb = new StringBuilder();
    sb.AppendFormat("Event : {0}\n", "OnTextDrawingItemClickedEvent");
    sb.AppendFormat("ID : {0}\n", e.Item.ID);
    sb.AppendFormat("Center : {0}\n", e.Item.Center.ToString());
    sb.AppendFormat("Direction : {0}\n", e.Item.Direction.ToString());
    sb.AppendFormat("UpDirection : {0}\n", e.Item.UpDirection.ToString());
    sb.AppendFormat("Height : {0}\n", e.Item.Height);
    sb.AppendFormat("Color : {0}\n", e.Item.FontColor.ToString());
    sb.AppendFormat("Text : {0}\n", e.Item.Text);
    System.Diagnostics.Trace.WriteLine(sb.ToString());
}


private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void DrawText()
{
    VIZCore3D.NET.Data.TextDrawingItem item = vizcore3d.TextDrawing.Add(
        new VIZCore3D.NET.Data.Vertex3D(0, 0, 0)
        , new VIZCore3D.NET.Data.Vector3D(1, 0, 0) /* X+ 방향으로 텍스트가 표시 */
        , new VIZCore3D.NET.Data.Vector3D(0, 1, 0) /* 텍스트의 위쪽 방향 : Z+에서 볼때, Y+가 위쪽 임. */
        , 3500
        , Color.Black
        , "ZONE_001"
        );

    vizcore3d.TextDrawing.Show(item.ID, true);
}


private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void DrawText()
{
    VIZCore3D.NET.Data.TextDrawingItem item = vizcore3d.TextDrawing.Add(
        new VIZCore3D.NET.Data.Vertex3D(0, 0, 0)
        , new VIZCore3D.NET.Data.Vector3D(1, 0, 0) /* X+ 방향으로 텍스트가 표시 */
        , new VIZCore3D.NET.Data.Vector3D(0, 1, 0) /* 텍스트의 위쪽 방향 : Z+에서 볼때, Y+가 위쪽 임. */
        , 3500
        , 100
        , Color.Black
        , "ZONE_001"
        );

    vizcore3d.TextDrawing.Show(item.ID, true);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // 모델 회전 후, 회전 각도 조회
    vizcore3d.Object3D.Transform.OnObjectRotatedEvent += Transform_OnObjectRotatedEvent;

    // 개체 회전 다이얼로그 실행
    // 혹은 사용자가 모델트리의 Context Menu에서 모델 회전 수행
    vizcore3d.Object3D.Transform.RotateDialog();
}

private void Transform_OnObjectRotatedEvent(object sender, Event.EventManager.ObjectRotatedEventArgs e)
{
    StringBuilder sb = new StringBuilder();
    sb.AppendFormat("Event : {0}\n", "OnObjectRotatedEvent");
    sb.AppendFormat("Node : {0}\n", e.Objects.Count);
    sb.AppendFormat("X : {0}\n", e.AngleX);
    sb.AppendFormat("Y : {0}\n", e.AngleY);
    sb.AppendFormat("Z : {0}\n", e.AngleZ);
    System.Diagnostics.Trace.WriteLine(sb.ToString());
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // 모델 이동 후, 이동 거리 조회
    vizcore3d.Object3D.Transform.OnObjectMovedEvent += Transform_OnObjectMovedEvent;

    // 개체 이동 다이얼로그 실행
    // 혹은 사용자가 모델트리의 Context Menu에서 모델 이동 수행
    vizcore3d.Object3D.Transform.MoveDialog();
}

private void Transform_OnObjectMovedEvent(object sender, Event.EventManager.ObjectMovedEventArgs e)
{
    StringBuilder sb = new StringBuilder();
    sb.AppendFormat("Event : {0}\n", "OnObjectMovedEvent");
    sb.AppendFormat("Node : {0}\n", e.Objects.Count);
    sb.AppendFormat("X : {0}\n", e.DistanceX);
    sb.AppendFormat("Y : {0}\n", e.DistanceY);
    sb.AppendFormat("Z : {0}\n", e.DistanceZ);
    System.Diagnostics.Trace.WriteLine(sb.ToString());
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP).Count == 0) return;

    // Handle Mode
    //vizcore3d.Object3D.Transform.HandleMode = Manager.TransformManager.HandleModes.Default;       // 기본
    vizcore3d.Object3D.Transform.HandleMode = Manager.TransformManager.HandleModes.MoveByAxis;      // 축 기준 이동
    //vizcore3d.Object3D.Transform.HandleMode = Manager.TransformManager.HandleModes.MoveByPlane;   // 평행면 이동
    //vizcore3d.Object3D.Transform.HandleMode = Manager.TransformManager.HandleModes.RotateByAxis;  // 축 기준 회전

    // Enable Handle
    vizcore3d.Object3D.Transform.EnableHandle = true;
}


