// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

public void Example()
{
    List<Data.Node> node = vizcore3d.Object3D.FromFilter(Data.Object3dFilter.SELECTED_TOP);
    if (node.Count == 0) return;

    VIZCore3D.NET.Data.Matrix3D matrix = new Data.Matrix3D();
    matrix.SetRotateX(45);
    matrix.SetTranslate(1000.0f, 0.0f, 0.0f, false);

    vizcore3d.Object3D.Transform.Transform(node, matrix, true);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    string key = "TYPE";
    string val = "PIPE";

    List<int> node = new List<int>() { 1, 4, 5, 7, 9, 100, 200 };

    Dictionary<int, bool> result =
        vizcore3d.Object3D.UDA.ContainsNode(
            key     /* UDA::KEY */
            , val   /* UDA::VALUE */
            , node  /* Node List */
            , true  /* True(Bottom Up), False(Current Node Index) */
            );

    foreach (KeyValuePair<int, bool> item in result)
    {
        if(item.Value == true)
        {
            System.Console.WriteLine("Contain");
        }
        else
        {
            System.Console.WriteLine("Does Not Contain");
        }
    }
}


