// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example1()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    List<VIZCore3D.NET.Data.Node> nodes
        = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP);
    if (nodes.Count == 0) return;

    VIZCore3D.NET.Data.BoundBox3D boundBox = vizcore3d.Object3D.GetBoundBox(nodes);

    VIZCore3D.NET.Data.Section section = vizcore3d.Section.AddBox(false);
    vizcore3d.Section.SetBoxSize(section.ID, boundBox);

    vizcore3d.View.FitToView(boundBox.GetCenter());
}

private void Example2()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    List<VIZCore3D.NET.Data.Node> nodes
        = vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP);
    if (nodes.Count == 0) return;

    VIZCore3D.NET.Data.Section section = vizcore3d.Section.AddBox(true);

    vizcore3d.View.FitToView(section.BoundBox.GetCenter());
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example1()
{
    VIZCore3D.NET.Data.BoundBox3D boundBox = vizcore3d.Model.BoundBox;

    vizcore3d.View.BoxZoom(
        boundBox.GetVertexMin()
        , boundBox.GetVertexMax()
        );
}

private void Example2()
{
    VIZCore3D.NET.Data.BoundBox3D boundBox = vizcore3d.Model.BoundBox;

    VIZCore3D.NET.Data.Vertex3D min = vizcore3d.View.WorldToScreen(boundBox.GetVertexMin(), true);
    VIZCore3D.NET.Data.Vertex3D max = vizcore3d.View.WorldToScreen(boundBox.GetVertexMax(), true);

    vizcore3d.View.BoxZoom(
        Convert.ToInt32(min.X)
        , Convert.ToInt32(min.Y)
        , Convert.ToInt32(max.X)
        , Convert.ToInt32(max.Y)
        );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example1()
{
    VIZCore3D.NET.Data.BoundBox3D boundBox = vizcore3d.Model.BoundBox;

    vizcore3d.View.BoxZoom(
        boundBox.GetVertexMin()
        , boundBox.GetVertexMax()
        );
}

private void Example2()
{
    VIZCore3D.NET.Data.BoundBox3D boundBox = vizcore3d.Model.BoundBox;

    VIZCore3D.NET.Data.Vertex3D min = vizcore3d.View.WorldToScreen(boundBox.GetVertexMin(), true);
    VIZCore3D.NET.Data.Vertex3D max = vizcore3d.View.WorldToScreen(boundBox.GetVertexMax(), true);

    vizcore3d.View.BoxZoom(
        Convert.ToInt32(min.X)
        , Convert.ToInt32(min.Y)
        , Convert.ToInt32(max.X)
        , Convert.ToInt32(max.Y)
        );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    vizcore3d.View.MoveCamera(
        100.0f      /* X */
        , 200.0f    /* Y */
        , 300.0f    /* Z */
        , 1000.0f   /* Distance */
        , true      /* Reset View */
    );
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Camera Position
    VIZCore3D.NET.Data.Vertex3D camera = new Data.Vertex3D(18200.0f, 4095.0f, 17354.0f);

    vizcore3d.View.Projection = Data.Projections.Perspective;
    vizcore3d.View.FOV = Convert.ToSingle(60.0f);
    vizcore3d.View.SetPivotPosition(camera);
    vizcore3d.Walkthrough.LockZAxis = false;
    vizcore3d.View.MoveCamera(camera, 0.0f, false);

    float degree = 5.0f;

    // Camera Direction
    // Up(0), Down(1), Left(2), Right(3)
    int cameraDir = 0;

    if(cameraDir == 0)
        vizcore3d.View.RotateCameraByAxis(Data.BaseAxis.LOCAL, Data.Axis.X, Data.Directions.PLUS, degree);
    else if(cameraDir == 1)
        vizcore3d.View.RotateCameraByAxis(Data.BaseAxis.LOCAL, Data.Axis.X, Data.Directions.MINUS, degree);
    else if (cameraDir == 2)
        vizcore3d.View.RotateCameraByAxis(Data.BaseAxis.WORLD, Data.Axis.Z, Data.Directions.PLUS, degree);
    else if (cameraDir == 3)
        vizcore3d.View.RotateCameraByAxis(Data.BaseAxis.WORLD, Data.Axis.Z, Data.Directions.MINUS, degree);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Image : Current View
    System.Drawing.Image img = vizcore3d.View.CaptureImage();
}

private void CaptureImageToClipboard()
{
    vizcore3d.View.CaptureImageToClipboard();
}

private void CaptureCustomSize(int width, int height)
{
    vizcore3d.View.BeginBackgroundRenderingMode(width, height);

    vizcore3d.View.RotateCamera(VIZCore3D.NET.Data.AxisDirection.Z_PLUS, 90);

    System.Drawing.Image resizedImg = vizcore3d.View.GetBackgroundRenderingImage();

    vizcore3d.View.EndBackgroundRenderingMode();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Image : Current View
    System.Drawing.Image img = vizcore3d.View.CaptureImage();
}

private void CaptureImageToClipboard()
{
    vizcore3d.View.CaptureImageToClipboard();
}

private void CaptureCustomSize(int width, int height)
{
    vizcore3d.View.BeginBackgroundRenderingMode(width, height);

    vizcore3d.View.RotateCamera(VIZCore3D.NET.Data.AxisDirection.Z_PLUS, 90);

    System.Drawing.Image resizedImg = vizcore3d.View.GetBackgroundRenderingImage();

    vizcore3d.View.EndBackgroundRenderingMode();
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    int width = 800;
    int height = 600;

    // 메모리 기반 렌더링 모드 시작
    vizcore3d.View.BeginBackgroundRenderingMode(width, height);

    // 화면에 가시화하지 않고, 메모리 상태에서 렌더링 및 화면 이미지 반환
    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.ISO_PLUS);
    System.Drawing.Image img1 = vizcore3d.View.GetBackgroundRenderingImage();
    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.ISO_MINUS);
    System.Drawing.Image img2 = vizcore3d.View.GetBackgroundRenderingImage();

    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.X_PLUS);
    System.Drawing.Image img3 = vizcore3d.View.GetBackgroundRenderingImage();
    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.X_MINUS);
    System.Drawing.Image img4 = vizcore3d.View.GetBackgroundRenderingImage();

    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.Y_PLUS);
    System.Drawing.Image img5 = vizcore3d.View.GetBackgroundRenderingImage();
    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.Y_MINUS);
    System.Drawing.Image img6 = vizcore3d.View.GetBackgroundRenderingImage();

    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.Z_PLUS);
    System.Drawing.Image img7 = vizcore3d.View.GetBackgroundRenderingImage();
    vizcore3d.View.MoveCamera(VIZCore3D.NET.Data.CameraDirection.Z_MINUS);
