// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;

    List<int> index = new List<int>() { 100, 200, 300 };

    System.Drawing.Image img = vizcore3d.View.GetImage(
        index   /* Node Index */
        , 800   /* Width */
        , 600   /* Height */
        );
    if (img == null) return;

    SaveFileDialog dlg = new SaveFileDialog();
    dlg.Filter = "Image (.png)|*.png";
    if (dlg.ShowDialog() != DialogResult.OK) return;

    img.Save(dlg.FileName);

    VIZCore3D.NET.Utility.ExplorerHelper.Show(dlg.FileName);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    if (vizcore3d.Model.IsOpen() == false) return;
    if (vizcore3d.Object3D.FromFilter(VIZCore3D.NET.Data.Object3dFilter.SELECTED_TOP).Count == 0) return;

    System.Drawing.Image img = vizcore3d.View.GetImage(
        800     /* Width */
        , 600   /* Height */
        );
    if (img == null) return;

    SaveFileDialog dlg = new SaveFileDialog();
    dlg.Filter = "Image (.png)|*.png";
    if (dlg.ShowDialog() != DialogResult.OK) return;

    img.Save(dlg.FileName);

    VIZCore3D.NET.Utility.ExplorerHelper.Show(dlg.FileName);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    int x = 300;
    int y = 400;

    VIZCore3D.NET.Data.PickedSurfaceData vector = 
        vizcore3d.View.GetPickedSurfaceNormalVector(x, y);

    // 선택된 부재가 없음
    if (vector.Picked == false) return;

    // 표면점이 없음
    if (vector.Surface == false) return;

    // 선택된 지점
    VIZCore3D.NET.Data.Vertex3D position = vector.Position;

    // 선택된 지점의 Normal Vector
    VIZCore3D.NET.Data.Vector3D normal = vector.Normal;
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    System.Drawing.Point point = new Point(300, 400);

    VIZCore3D.NET.Data.PickedSurfaceData vector = 
        vizcore3d.View.GetPickedSurfaceNormalVector(point);

    // 선택된 부재가 없음
    if (vector.Picked == false) return;

    // 표면점이 없음
    if (vector.Surface == false) return;

    // 선택된 지점
    VIZCore3D.NET.Data.Vertex3D position = vector.Position;

    // 선택된 지점의 Normal Vector
    VIZCore3D.NET.Data.Vector3D normal = vector.Normal;
}


