// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // ROOT
    // ---- A000
    // -------A100 + FILE
    // -------A200 + NODE
    // ---- B000
    // -------B100 + FILE
    // -------B200 + NODE
    // ---- C000
    // -------C100 + FILE
    // -------C200 + NODE

    VIZCore3D.NET.Manager.VIZXMLManager vizxml = new VIZCore3D.NET.Manager.VIZXMLManager("ROOT");

    VIZCore3D.NET.Manager.VIZXMLNode a000 = new VIZCore3D.NET.Manager.VIZXMLNode("A000");
    vizxml.AddNode(a000);

    a000.AddNode(new Manager.VIZXMLNode("A100", "C:\\Model\\A100.viz"));
    a000.AddNode(new Manager.VIZXMLNode("A200", "C:\\Model\\A200.viz", "ROOT\\BLOCK\\NODE"));

    VIZCore3D.NET.Manager.VIZXMLNode b000 = new VIZCore3D.NET.Manager.VIZXMLNode("b000");
    vizxml.AddNode(b000);

    b000.AddNode(new Manager.VIZXMLNode("B100", "C:\\Model\\B100.viz"));
    b000.AddNode(new Manager.VIZXMLNode("B200", "C:\\Model\\B200.viz", "ROOT\\BLOCK\\NODE"));

    VIZCore3D.NET.Manager.VIZXMLNode c000 = new VIZCore3D.NET.Manager.VIZXMLNode("c000");
    vizxml.AddNode(c000);

    c000.AddNode(new Manager.VIZXMLNode("C100", "C:\\Model\\C100.viz"));
    c000.AddNode(new Manager.VIZXMLNode("C200", "C:\\Model\\C200.viz", "ROOT\\BLOCK\\NODE"));

    vizxml.ExportVIZXML("C:\\Model\\Export.vizxml");

    vizcore3d.Model.Open("C:\\Model\\Export.vizxml");
}


