    VIZCore3D.NET.Manager.VIZXMLNode A11A0 = B1010.AddNode(new VIZCore3D.NET.Manager.VIZXMLNode("A11A0"));
    VIZCore3D.NET.Manager.VIZXMLNode A830A = A11A0.AddNode(new VIZCore3D.NET.Manager.VIZXMLNode("A830A", "C:\\A830A.viz"));
    VIZCore3D.NET.Manager.VIZXMLNode A111A = A11A0.AddNode(new VIZCore3D.NET.Manager.VIZXMLNode("A111A", "C:\\A111A.viz"));
    VIZCore3D.NET.Manager.VIZXMLNode A112A = A11A0.AddNode(new VIZCore3D.NET.Manager.VIZXMLNode("A112A", "C:\\A112A.viz", "A112A\\BLOCK"));
