// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void MoveToolbarItem()
{
    // Main Toolbar Item Count
    int maxIndex = vizcore3d.ToolbarMain.Items.Count;

    // Add Separator Control
    vizcore3d.ToolbarMain.Items.Add(new ToolStripSeparator());
    maxIndex++;

    // Note Toolbar Item Count
    int count = vizcore3d.ToolbarNote.Items.Count;

    // Insert Item
    for (int i = count; i > 0; i--)
    {
        // Insert : Moved Item
        vizcore3d.ToolbarMain.Items.Insert(
            maxIndex // Index to be inserted
            , vizcore3d.ToolbarNote.Items[i - 1] // Item Index
            );
    }

    // Hide Toolbar : Empty Item
    vizcore3d.ToolbarNote.Visible = false;
}

private void AddNewItem()
{
    // Add Separator Control
    vizcore3d.ToolbarMain.Items.Add(new ToolStripSeparator());

    ToolStripItem item = new ToolStripMenuItem();

    //Name that will apear on the menu
    item.Text = "New Button";

    //Put in the Name property whatever neccessery to retrive your data on click event
    item.Name = "btnNewItem";

    //On-Click event
    item.Click += Item_Click;

    //Add the submenu to the parent menu
    vizcore3d.ToolbarMain.Items.Add(item);
}

private void Item_Click(object sender, EventArgs e)
{
    MessageBox.Show("Clicked.", "VIZCore3D.NET", MessageBoxButtons.OK, MessageBoxIcon.Information);
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    // Add Mouse Operation
    vizcore3d.MouseControl = vizcore3d.MouseControl | VIZCore3D.NET.MouseControls.Move;
    vizcore3d.MouseControl = vizcore3d.MouseControl | VIZCore3D.NET.MouseControls.Wheel;

    // Remove Mouse Operation
    vizcore3d.MouseControl = vizcore3d.MouseControl & ~VIZCore3D.NET.MouseControls.Move;
    vizcore3d.MouseControl = vizcore3d.MouseControl & ~VIZCore3D.NET.MouseControls.Wheel;

    // Has Flag
    if (vizcore3d.MouseControl.HasFlag(VIZCore3D.NET.MouseControls.Move) == true)
        vizcore3d.MouseControl = vizcore3d.MouseControl & ~VIZCore3D.NET.MouseControls.Move;
    else
        vizcore3d.MouseControl = vizcore3d.MouseControl | VIZCore3D.NET.MouseControls.Move;
}


// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example()
{
    //===================================
    // Environment
    //===================================
    string assemblyPath = VIZCore3D.NET.ModuleInitializer.GetAssemblyLocation();
    string entryAssemblyPath = VIZCore3D.NET.ModuleInitializer.GetEntryAssemblyLocation();

    string assemblyVersion = VIZCore3D.NET.ModuleInitializer.GetLibraryVersion();
    string interopVersion = VIZCore3D.NET.ModuleInitializer.GetInteropLibraryVersion();
    bool compareVersion = VIZCore3D.NET.ModuleInitializer.CompareVersion();

    bool existLibrary = VIZCore3D.NET.ModuleInitializer.ExistLibrary();

    // Initialize VIZCore3D.NET
    // Case 1. : Extract Library by Condition
    //if(existLibrary == false || compareVersion == false)
    //    VIZCore3D.NET.ModuleInitializer.Run();
    // Case 2. : Extract Library Always
    VIZCore3D.NET.ModuleInitializer.Run();

    vizcore3d = new VIZCore3D.NET.VIZCore3DControl();

    vizcore3d.Dock = DockStyle.Fill;
    panelView.Controls.Add(vizcore3d);

    // Event
    vizcore3d.OnInitializedVIZCore3D += VIZCore3D_OnInitializedVIZCore3D;
}

private void VIZCore3D_OnInitializedVIZCore3D(object sender, EventArgs e)
{
    VIZCore3D.NET.Data.LicenseResults result =
        vizcore3d.License.LicenseServer("0.0.0.0", 8901);

    if (result != Data.LicenseResults.SUCCESS)
    {
        MessageBox.Show(result.ToString()
        , "VIZCore3D.NET"
        , MessageBoxButtons.OK
        , MessageBoxIcon.Error
        );

        return;
    }

    // Init. Some Options...
}


