private void Example()
{
    // Backup Status
    bool status = vizcore3d.EnableWaitForm;

    // Disable Wait Form
    vizcore3d.EnableWaitForm = false;

    // Show Custom Wait Form(Dialog)
    vizcore3d.ShowWaitForm();

    vizcore3d.UpdateWaitForm("Please Wait...", "Processing...");

    for (int i = 0; i < 100; i++)
    {
        // Some code here...
    }

    // Hide Custom Wait Form(Dialog)
    vizcore3d.CloseWaitForm();

    // Restore Status
    vizcore3d.EnableWaitForm = status;
}


