// VIZCore3D.NET Control
private VIZCore3D.NET.VIZCore3DControl vizcore3d;

private void Example(List<VIZCore3D.NET.Data.Node> items)
{
    // 정보패널(Information Panel)에 추가하고자 하는 외부 컨트롤
    System.Windows.Forms.UserControl customCtrl 
        = new System.Windows.Forms.UserControl();

    ShowInformationPanel(customCtrl);
}

private void ShowInformationPanel(UserControl ctrl)
{
    ctrl.Dock = DockStyle.Fill;

    // 기존 컨트롤이 있는 경우, 제거
    if (vizcore3d.InformationPanel.Controls.Count != 0)
        vizcore3d.InformationPanel.Controls.Clear();

    // 새로운 컨트롤 삽입
    vizcore3d.InformationPanel.Controls.Add(ctrl);

    // 조회
    vizcore3d.InformationPanel.Visible = true;
}

private void HideInformationPanel()
{
    // 숨기기
    vizcore3d.InformationPanel.Visible = false;
}


